package test_projects;

public class PosAndNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=0;
		
		if(num>0) {
			System.out.println("Positive");
		}
		else if(num<0) {
			System.out.println("Negative");
		}
		else {
			System.out.println("Zero");
		}
	}

}
